import { FC, useEffect, useRef, useState } from "react";
import { EntityType } from "../types/entity-type";
import { getOrderbook } from "../api/api";
import { OrderBookType } from "../types/order-book-type";
import { getNumberRounded } from "../utils/get-number-rounded";

type OrderBookProps = {
  entity: EntityType;
};

export const OrderBook: FC<OrderBookProps> = ({ entity }) => {
  const [orderBookValues, setOrderBookValues] = useState<OrderBookType>();
  const [isLoading, setIsLoading] = useState(false);

  const asks = orderBookValues?.asks ?? [];
  const reversedAsks = [...asks].reverse();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const response = await getOrderbook(entity);
        setOrderBookValues(response);
      } catch (err) {
        console.error("Error fetching order book", err);
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, [entity]);

  const scrollableDivRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (scrollableDivRef.current) {
      // Calculate the initial scrollTop position to center the content
      const scrollableDiv = scrollableDivRef.current;
      const scrollTop = (scrollableDiv.scrollHeight - scrollableDiv.clientHeight) / 2;
      scrollableDiv.scrollTop = scrollTop;
    }
  }, []);

  return (
    <div className="w-72 bg-white rounded-md pb-4 p-2">
      <div className="p-1">
        <div className="flex justify-between px-4">
          <p className="font-bold">Price</p>
          <p className="font-bold">Amount ({entity})</p>
        </div>
        {/* fix loading state */}
        <div ref={scrollableDivRef} className="bg-white h-96 overflow-y-scroll">
          {isLoading ? (
            <div>LOADING</div>
          ) : (
            <>
              {reversedAsks?.map((ask) => {
                return (
                  <div className="flex gap-6 pb-1 px-4 justify-between">
                    <p className="text-red-600">
                      {parseFloat(ask[0]).toFixed(3)}
                    </p>
                    <p>{parseFloat(ask[1]).toFixed(5)}</p>
                  </div>
                );
              })}
              {orderBookValues?.bids.map((bid) => {
                return (
                  <div className="flex gap-6 pb-1 px-4 justify-between">
                    <p className="text-green-600">
                      {getNumberRounded(bid[0], 3)}
                    </p>
                    <p>{getNumberRounded(bid[1], 5)}</p>
                  </div>
                );
              })}
            </>
          )}
        </div>
      </div>
    </div>
  );
};
