import { FC, useEffect, useState } from "react"
import { EntityType } from "../types/entity-type"
import { getOrderbook } from "../api/api"
import { OrderBookType } from "../types/order-book-type"

type OrderBookProps = {
    entity: EntityType
}

export const OrderBook: FC<OrderBookProps> = ({ entity }) => {
    const [orderBookValues, setOrderBookValues] = useState<OrderBookType>()

    const asks = orderBookValues?.asks.reverse()
    
    useEffect(() => {
        const fetchData = async() => {
            try {
                const response = await getOrderbook(entity)
                setOrderBookValues(response)
            } catch (err) {
                console.error('Error fetching order book', err)
            }
        }
        fetchData()
    }, [entity])


    return (
        <div>
            <div className="bg-white overflow-y-scroll">
                {asks?.map(ask => {
                    return (
                        <div className="flex gap-6">
                        <p className="text-red-600">{parseFloat(ask[0]).toFixed(3)}</p>
                        <p>{parseFloat(ask[1]).toFixed(5)}</p>
                        </div>
                    )
                })}
                {orderBookValues?.bids.map(bid => {
                    return (
                        <div className="flex gap-6">
                        <p className="text-green-600">{parseFloat(bid[0]).toFixed(3)}</p>
                        <p>{parseFloat(bid[1]).toFixed(5)}</p>
                        </div>
                    )
                })}
            </div>
        </div>
    )
}
