import { FC, useEffect, useState } from "react";
import { EntityType } from "../types/entity-type";
import { SideType } from "../types/side-type";
import { SideToggle } from "./side-toggle";
import { sendTrade } from "../api/api";

type OrderFormProps = {
  entity: EntityType;
};

export const OrderForm: FC<OrderFormProps> = ({ entity }) => {
  const [price, setPrice] = useState(0);
  const [amount, setAmount] = useState(1);
  const [side, setSide] = useState<SideType>("BUY");
  const [priceErrorMessage, setPriceErrorMessage] = useState("");
  const [amountErrorMessage, setAmountErrorMessage] = useState("");
  const [confirmationMessage, setConfirmationMessage] = useState("");

  const toggleSideValue = (side: SideType) => {
    setSide(side);
  };

  const notional = price * amount;
  const hasFormError = !!priceErrorMessage || !!amountErrorMessage;

  useEffect(() => {
    if (price <= 0) {
      setPriceErrorMessage("Must specify a price greater than 0.");
    } else {
      setPriceErrorMessage("");
    }
    if (amount < 1) {
      setAmountErrorMessage("Must specify an amount greater than 1.");
    } else {
      setAmountErrorMessage("");
    }
  }, [price, amount]);

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    const fetchData = async () => {
      try {
        const response = await sendTrade({
          asset: entity,
          side: side,
          type: "LIMIT",
          quantity: amount,
          price: price,
          notional: notional,
        });
        setConfirmationMessage(
          `You have successfully submitted an order to ${side} ${amount} ${entity} at ${price} for ${notional}`
        );
        console.log(response);
      } catch (err) {
        console.error("Error fetching order book", err);
      } finally {
      }
    };
    fetchData();
  };

  return (
    <div className="rounded-md bg-white h-3/4 w-80">
      <SideToggle side={side} toggleSideValue={toggleSideValue} />
      <form
        onSubmit={handleSubmit}
        className="h-4/5 flex flex-col justify-between items-center gap-4"
      >
        <div className="flex gap-4">
          <div className="flex flex-col gap-4 pt-6">
            <label className="font-semibold">Price:</label>
            <label className="font-semibold">Amount:</label>
            <label className="font-semibold">Total:</label>
          </div>
          <div className="flex flex-col gap-4 pt-6">
            <input
            className="shadow border rounded focus:outline-none focus:border-gray-500"
              type="number"
              value={price}
              onChange={(event) => setPrice(Number(event.target.value))}
            />
            <input
              type="number"
              value={amount}
              onChange={(event) => setAmount(Number(event.target.value))}
            />
            <input
              type="number"
              value={amount}
              onChange={(event) => setAmount(Number(event.target.value))}
            />
          </div>
        </div>
        <div className="p-1 text-red-600 font-semibold">
          {priceErrorMessage && (
            <p>{priceErrorMessage}</p>
          )}
          {amountErrorMessage && (
            <p>{amountErrorMessage}</p>
          )}
        </div>
        <button
          className={`justify-self-end w-1/3 h-auto p-2 border rounded-md font-semibold shadow-md 
          ${
            side === "BUY"
              ? hasFormError
                ? "text-gray-300 cursor-not-allowed"
                : "border-green-500 text-green-500 hover:bg-green-500 hover:text-white active:bg-green-700"
              : hasFormError
              ? "text-gray-300 cursor-not-allowed"
              : "border-red-500 text-red-500 hover:bg-red-500 hover:text-white active:bg-red-700"
          }`}
          type="submit"
          disabled={hasFormError}
        >
          {side}
        </button>
      </form>
    </div>
  );
};
