import React, { FC } from "react";
import { SideType } from "../types/side-type";

type TabProps = {
  title: string;
  isActive: boolean;
  onClick: () => void;
};

type EntityToggleProps = {
  side: SideType;
  toggleSideValue: (side: SideType) => void;
};

const Tab: FC<TabProps> = ({ title, isActive, onClick }) => {
  return (
    <button
      className={`font-bold ${
        isActive ? "border-b-2 border-green-500" : ""
      } px-4 py-2 focus:outline-none`}
      onClick={onClick}
    >
      {title}
    </button>
  );
};

export const SideToggle: FC<EntityToggleProps> = ({
  side,
  toggleSideValue,
}) => {
  return (
    <div className="w-full">
      <div className="flex">
        <Tab
          title="BUY"
          isActive={side === "BUY"}
          onClick={() => toggleSideValue("BUY")}
        />
        <Tab
          title="SELL"
          isActive={side === "SELL"}
          onClick={() => toggleSideValue("SELL")}
        />
      </div>
    </div>
  );
};
