import { FC, useState } from "react";
import { EntityType } from "../types/entity-type";
import { SideType } from "../types/side-type";
import { SideToggle } from "./side-toggle";

type OrderFormProps = {
    entity: EntityType

}

export const OrderForm: FC<OrderFormProps> = ({ entity }) => {
  const [price, setPrice] = useState(0);
  const [amount, setAmount] = useState(0);
  const [side, setSide] = useState<SideType>('BUY')
  const [errorMessage, setErrorMessage] = useState('')

  const toggleSideValue = (side: SideType) => {
    setSide(side)
  }

  const notional = price * amount

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      // submit order
    };

  return (
    <div className="rounded-md border border-grey">
      <SideToggle side={side} toggleSideValue={toggleSideValue}/>
      <form onSubmit={handleSubmit}>
        <div>
          <label className="font-semibold">Price:</label>
          <input
            type="text"
            value={price}
            onChange={(event) => setPrice(Number(event.target.value))}
          />
        </div>
        <div className="flex gap-4">
          <label className="font-semibold">Amount:</label>
          <input
            type="text"
            value={amount}
            onChange={(event) => setAmount(Number(event.target.value))}
          />
        </div>
        <div className="flex">
            <label className="font-semibold">Notional:</label>
            <p>{notional}</p>
        </div>
      {errorMessage && (
        <div>
          
        </div>
      )}
        <button className={`w-auto h-auto p-2 border rounded-md ${side === 'BUY' ? 'border-green-500' : 'border-red-500'}`} type="submit">{side}</button>
      </form>
    </div>
  );
};
