import React, { FC, useState } from 'react';
import { EntityType } from '../types/entity-type';

type TabProps = {
    title: string
    isActive: boolean
    onClick: () => void
}

type EntityToggleProps = {
    entity: EntityType
    toggleEntityValue: (entity: EntityType) => void
}

const Tab: FC<TabProps> = ({ title, isActive, onClick }) => {
  return (
    <button
      className={`font-bold ${
        isActive ? 'border-b-2 border-green-500' : ''
      } px-4 py-2 focus:outline-none`}
      onClick={onClick}
    >
      {title}
    </button>
  );
};

export const EntityToggle: FC<EntityToggleProps> = ({ entity, toggleEntityValue }) => {
  const [activeTab, setActiveTab] = useState(1);

  const handleTabClick = (tabIndex) => {
    setActiveTab(tabIndex);
  };

  return (
    <div className="max-w-md mx-auto">
      <div className="flex">
        <Tab
          title="BTC"
          isActive={activeTab === 1}
          onClick={() => handleTabClick(1)}
        />
        <Tab
          title="ETH"
          isActive={activeTab === 2}
          onClick={() => handleTabClick(2)}
        />
      </div>
      <div className="mt-4">
        {activeTab === 1 && <div>Content of Tab 1</div>}
        {activeTab === 2 && <div>Content of Tab 2</div>}
      </div>
    </div>
  );
};
