import React, { FC } from "react";
import { EntityType } from "../types/entity-type";

type TabProps = {
  title: string;
  isActive: boolean;
  onClick: () => void;
};

type EntityToggleProps = {
  entity: EntityType;
  toggleEntityValue: (entity: EntityType) => void;
};

const Tab: FC<TabProps> = ({ title, isActive, onClick }) => {
  return (
    <button
      className={`font-bold ${
        isActive ? "border-b-2 border-green-500" : ""
      } px-4 py-2 focus:outline-none`}
      onClick={onClick}
    >
      {title}
    </button>
  );
};

export const EntityToggle: FC<EntityToggleProps> = ({
  entity,
  toggleEntityValue,
}) => {

  return (
    <div className="w-full mx-auto">
      <div className="flex w-full justify-center">
        <Tab
          title="BTC"
          isActive={entity === 'BTC'}
          onClick={() => toggleEntityValue('BTC')}
        />
        <Tab
          title="ETH"
          isActive={entity === 'ETH'}
          onClick={() => toggleEntityValue('ETH')}
        />
      </div>
    </div>
  );
};
