import { FC, useEffect, useRef, useState } from "react";
import { EntityType } from "../types/entity-type";
import { getOrderbook } from "../api/api";
import { OrderBookType } from "../types/order-book-type";

type OrderBookProps = {
  entity: EntityType;
};

export const OrderBook: FC<OrderBookProps> = ({ entity }) => {
  const [orderBookValues, setOrderBookValues] = useState<OrderBookType>();
  const [isLoading, setIsLoading] = useState(false);

  const asks = orderBookValues?.asks ?? [];
  const reversedAsks = [...asks].reverse();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setIsLoading(true);
        const response = await getOrderbook(entity);
        setOrderBookValues(response);
      } catch (err) {
        console.error("Error fetching order book", err);
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, [entity]);

  const scrollableDivRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
      if (scrollableDivRef.current) {
        console.log(scrollableDivRef.current.scrollHeight / 2)
      scrollableDivRef.current.scrollTop = scrollableDivRef.current.scrollHeight / 2
    }
  }, []);

  return (
    <div>
      <div className="w-1/3">
        <div className="flex gap-6">
          <p className="font-bold">Price</p>
          <p className="font-bold">Amount ({entity})</p>
        </div>
        {/* fix loading state */}
        {isLoading && <div>LOADING</div>}
        {!isLoading && (
          <div ref={scrollableDivRef} className="bg-white h-80 overflow-y-scroll">
            {reversedAsks?.map((ask) => {
              return (
                <div className="flex gap-6 p-1">
                  <p className="text-red-600">
                    {parseFloat(ask[0]).toFixed(3)}
                  </p>
                  <p>{parseFloat(ask[1]).toFixed(5)}</p>
                </div>
              );
            })}
            {orderBookValues?.bids.map((bid) => {
              return (
                <div className="flex gap-6  p-1">
                  <p className="text-green-600">
                    {parseFloat(bid[0]).toFixed(3)}
                  </p>
                  <p>{parseFloat(bid[1]).toFixed(5)}</p>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};
